import React, {Component} from 'react';
import axios from 'axios';
// import DatePicker from 'react-datepicker';
// import "react-datepicker/dist/react-datepicker.css";

//If it did use the same port a.k.a the same "url", then the calling
//object would return the JSON but it wouldn;t really do anything but the
//react part would just display whatever component is specified.
export default class EditSpace extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeLocation = this.onChangeLocation.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: '',
      description: '',
      location: '',
      users: []
    }
  }
  componentDidMount() {
    axios.get('http://localhost:3000/spaces/' + this.props.match.params.id).then(response => {
      this.setState({username: response.data.username, description: response.data.description, location: response.data.location})
    }).catch(function(error) {
      console.log(error);
    });
    axios.get('http://localhost:3000/users/').then(response => {
      if (response.data.length > 0) {
        this.setState({
          users: response.data.map(user => user.username)
        })
      }
    }).catch((error) => {
      console.log(error);
    })

  }

  onChangeUsername(e) {
    this.setState({username: e.target.value})
  }

  onChangeDescription(e) {
    this.setState({description: e.target.value})
  }

  onChangeLocation(e) {
    this.setState({location: e.target.value})
  }

  onSubmit(e) {
    e.preventDefault();

    const space = {
      username: this.state.username,
      description: this.state.description,
      location: this.state.location
    }

    console.log(space);

    axios.post('http://localhost:3000/spaces/update/' + this.props.match.params.id, space).then(res => console.log(res.data));

    window.location = '/';
  }

  render() {
    var margincss = {
      'margin': '100'
    };
    return (<div>
      <h3 style={{textAlign: "center"}}>Edit Exercise Log</h3>
      <form onSubmit={this.onSubmit} style = {margincss}>
        <div className="form-group">
          <label>Username:
          </label>
          <select ref="userInput" required="required" className="form-control" value={this.state.username} onChange={this.onChangeUsername}>
            {
              this.state.users.map(function(user) {
                return <option key={user} value={user}>{user}
                </option>;
              })
            }
          </select>
        </div>
        <div className="form-group">
          <label>Description:
          </label>
          <input type="text" required="required" className="form-control" value={this.state.description} onChange={this.onChangeDescription}/>
        </div>
        <div className="form-group">
          <label>Location:
          </label>
          <input type="text" className="form-control" value={this.state.location} onChange={this.onChangeLocation}/>
        </div>

        <div className="form-group" style={{textAlign: "center"}}>
          <input type="submit" value="Edit Space Log" className="btn btn-primary"/>
        </div>
      </form>
    </div>)
  }
}
