let momGithubUrl = 'https://github.com/furcan/Markers-On-Map-React';
let momGoogleMap = null;
let momGoogleMapAdjustZoom;
let momNewOptions;
let momMarkersArray = [];
// Markers On Map: Default Options off

// Markers On Map: Console Error Function on
const momConsoleError = (errorMessage) => {
