        const script = `<script async defer id="MOMScript" src="https://maps.googleapis.com/maps/api/js?key=${apiKey}${placesLibrary}"></script>`;
        const scriptRange = document.createRange();
        const body = document.getElementsByTagName('body')[0];
        scriptRange.selectNode(body);
